import React, { useState, useEffect, useCallback } from 'react';
import Calendar from 'react-calendar';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import 'react-calendar/dist/Calendar.css';
import './calendar.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8888/api';

const CalendarComponent = () => {
  const [date, setDate] = useState(new Date());
  const [allTodos, setAllTodos] = useState([]);
  const [todos, setTodos] = useState([]);  // Ïò§ÎäòÏùò ÏùºÏ†ï
  const [selectedDateTodos, setSelectedDateTodos] = useState([]);  // ÏÑ†ÌÉùÎêú ÎÇ†ÏßúÏùò ÏùºÏ†ï
  const [loading, setLoading] = useState(true);
  const [selectedDate, setSelectedDate] = useState(null);
  const [showModal, setShowModal] = useState(false);  // Î™®Îã¨ ÏÉÅÌÉú
  const [selectedTodo, setSelectedTodo] = useState(null);  // ÏÑ†ÌÉùÎêú ÏùºÏ†ï
  const [upcomingTodos, setUpcomingTodos] = useState([]); // Îã§Í∞ÄÏò§Îäî ÏùºÏ†ï
  const navigate = useNavigate();
  const user = JSON.parse(sessionStorage.getItem('user') || '{}');

  // Î°úÍ∑∏Ïù∏ Í≤ÄÏ¶ù
  useEffect(() => {
    if (!user.memId) {
      navigate('/');
    }
  }, [user, navigate]);

  // Ï†ÑÏ≤¥ ÏùºÏ†ï Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchAllTodos = useCallback(async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/todos/mem/${user.memId}`);
      const todosData = response.data;
      setAllTodos(todosData);
      filterTodosByToday(todosData);  // Ïò§ÎäòÏùò ÏùºÏ†ï ÌïÑÌÑ∞ÎßÅ
      filterUpcomingTodos(todosData);  // Îã§Í∞ÄÏò§Îäî ÏùºÏ†ï ÌïÑÌÑ∞ÎßÅ
      if (selectedDate) {
        filterTodosByDate(selectedDate, todosData);  // ÏÑ†ÌÉùÎêú ÎÇ†ÏßúÏóê ÎßûÎäî ÏùºÏ†ï ÌïÑÌÑ∞ÎßÅ
      } else {
        // ÏÑ†ÌÉùÎêú ÎÇ†ÏßúÍ∞Ä ÏóÜÎã§Î©¥, Í∏∞Î≥∏Ï†ÅÏúºÎ°ú Ïò§Îäò ÎÇ†ÏßúÎ°ú ÏÑ§Ï†ï
        setSelectedDate(new Date().toISOString().split('T')[0]);
      }
    } catch (error) {
      alert('ÏùºÏ†ïÏùÑ Í∞ÄÏ†∏Ïò§Îäî Ï§ë Î¨∏Ï†úÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
    } finally {
      setLoading(false);
    }
  }, [user.memId, selectedDate]);

 // Ïò§ÎäòÏùò ÏùºÏ†ï ÌïÑÌÑ∞ÎßÅ
const filterTodosByToday = (data) => {
  const today = new Date().toISOString().split('T')[0]; // Ïò§Îäò ÎÇ†Ïßú
  const filtered = data.filter((todo) => {
    const startDate = new Date(todo.startDate);
    const endDate = new Date(todo.endDate);
    
    // ÏãúÏûëÏùºÍ≥º Ï¢ÖÎ£åÏùº ÏãúÍ∞ÑÏùÑ 00:00:00 Î∞è 23:59:59Î°ú ÎßûÏ∂∞Ï§çÎãàÎã§.
    startDate.setHours(0, 0, 0, 0);
    endDate.setHours(23, 59, 59, 999); // Ï¢ÖÎ£åÏùºÏùÑ 23:59:59Î°ú ÏÑ§Ï†ï
    
    // UTC Í∏∞Ï§ÄÏúºÎ°ú ÎÇ†Ïßú ÎπÑÍµê
    const startDateUtc = new Date(startDate.toUTCString());
    const endDateUtc = new Date(endDate.toUTCString());
    const todayUtc = new Date(today + "T00:00:00Z");

    return todayUtc >= startDateUtc && todayUtc <= endDateUtc;
  });
  setTodos(filtered);
};

// ÏÑ†ÌÉùÎêú ÎÇ†ÏßúÏùò ÏùºÏ†ï ÌïÑÌÑ∞ÎßÅ
const filterTodosByDate = (selectedDate, data) => {
  const selectedDateObj = new Date(selectedDate);
  selectedDateObj.setHours(0, 0, 0, 0);
  
  const filtered = data.filter((todo) => {
    const startDate = new Date(todo.startDate);
    const endDate = new Date(todo.endDate);
    
    startDate.setHours(0, 0, 0, 0); // ÏãúÏûëÏùº ÏãúÍ∞Ñ ÏÑ§Ï†ï
    endDate.setHours(23, 59, 59, 999); // Ï¢ÖÎ£åÏùºÏùÑ 23:59:59Î°ú ÏÑ§Ï†ï

    // UTC Í∏∞Ï§ÄÏúºÎ°ú ÎÇ†Ïßú ÎπÑÍµê
    const startDateUtc = new Date(startDate.toUTCString());
    const endDateUtc = new Date(endDate.toUTCString());
    const selectedDateUtc = new Date(selectedDate + "T00:00:00Z");

    return selectedDateUtc >= startDateUtc && selectedDateUtc <= endDateUtc;
  });
  setSelectedDateTodos(filtered);
};

  // Îã§Í∞ÄÏò§Îäî ÏùºÏ†ï ÌïÑÌÑ∞ÎßÅ
  const filterUpcomingTodos = (data) => {
    const upcoming = data.filter((todo) => new Date(todo.startDate) > new Date());
    setUpcomingTodos(upcoming.sort((a, b) => new Date(a.startDate) - new Date(b.startDate)));
  };

  // ÎÇ†Ïßú ÏÑ†ÌÉù Ìï∏Îì§Îü¨
  const handleDateChange = (date) => {
    const fixedDate = new Date(date);
    fixedDate.setHours(fixedDate.getHours() + 9); 
    fixedDate.setMinutes(0);
    fixedDate.setSeconds(0);
    fixedDate.setMilliseconds(0);
    setDate(fixedDate);
    const isoDate = fixedDate.toISOString().split('T')[0]; 
    setSelectedDate(isoDate);
  };

  // ÌéòÏù¥ÏßÄ Î°úÎìú Ïãú Ïò§Îäò ÎÇ†Ïßú ÏùºÏ†ï Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    fetchAllTodos();
  }, [fetchAllTodos]);

  useEffect(() => {
    if (selectedDate && allTodos.length > 0) {
      filterTodosByDate(selectedDate, allTodos);
    }
  }, [selectedDate, allTodos]);

  // Î™®Îã¨ Îã´Í∏∞
  const closeModal = () => {
    setShowModal(false);
    setSelectedTodo(null);
  };

  // ÏùºÏ†ï ÌÅ¥Î¶≠ Ïãú Î™®Îã¨ Ïó¥Í∏∞
  const openModal = (todo) => {
    setSelectedTodo(todo);
    setShowModal(true);
  };

  // ÏùºÏ†ï ÏÇ≠Ï†ú
  const handleDelete = async () => {
    if (!selectedTodo) return;
    try {
      await axios.delete(`${API_BASE_URL}/todos/${selectedTodo.todoId}`);
      setAllTodos(allTodos.filter(todo => todo.todoId !== selectedTodo.todoId));
      closeModal();
    } catch (error) {
      alert('ÏùºÏ†ï ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  // ÏùºÏ†ï ÏàòÏ†ï
  const handleEdit = () => {
    // ÏàòÏ†ï ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
    navigate('/edit-todo', { state: { todo: selectedTodo } });
    closeModal();
  };

  const handleLogout = () => {
    sessionStorage.removeItem('user');
    navigate('/');
  };

  // Calendar tileContentÎ•º ÏÇ¨Ïö©ÌïòÏó¨ ÎÇ†ÏßúÏóê ÏïÑÏù¥ÏΩò ÌëúÏãú
  const tileContent = ({ date, view }) => {
    if (view === 'month') {
      const dateStr = date.toISOString().split('T')[0]; // ÎÇ†Ïßú ÌòïÏãù: YYYY-MM-DD
      
      // Ìï¥Îãπ ÎÇ†ÏßúÏóê ÏùºÏ†ïÏù¥ ÏûàÎäîÏßÄ ÌôïÏù∏
      const hasTodo = allTodos.some(todo => {
        const startDate = new Date(todo.startDate);
        const endDate = new Date(todo.endDate);
        startDate.setHours(0, 0, 0, 0);
        endDate.setHours(23, 59, 59, 999);
        
        // ÎÇ†Ïßú ÎπÑÍµê: ÏùºÏ†ïÏùò ÏãúÏûëÏùº ÎòêÎäî Ï¢ÖÎ£åÏùºÏù¥ ÏÑ†ÌÉùÎêú ÎÇ†ÏßúÏôÄ ÏùºÏπòÌïòÎäîÏßÄ ÌôïÏù∏
        return startDate.toISOString().split('T')[0] === dateStr || endDate.toISOString().split('T')[0] === dateStr;
      });
      
      return hasTodo ? <span className="todo-icon">üìÖ</span> : null;
    }
    return null;
  };

  // Calendar tileClassNameÏùÑ ÏÇ¨Ïö©ÌïòÏó¨ ÏãúÏûëÏùºÍ≥º Ï¢ÖÎ£åÏùºÏùÑ ÏÑ†ÏúºÎ°ú Ïó∞Í≤∞
  const tileClassName = ({ date, view }) => {
    if (view === 'month') {
      const dateStr = date.toISOString().split('T')[0]; // ÎÇ†Ïßú ÌòïÏãù: YYYY-MM-DD
      
      // ÏùºÏ†ïÏùò ÏãúÏûëÏùºÍ≥º Ï¢ÖÎ£åÏùºÏùÑ ÌôïÏù∏ÌïòÏó¨ ÏÑ†ÏùÑ Ïó∞Í≤∞
      const startDateTodo = allTodos.find(todo => {
        const startDate = new Date(todo.startDate);
        startDate.setHours(0, 0, 0, 0);
        return startDate.toISOString().split('T')[0] === dateStr;
      });
      
      const endDateTodo = allTodos.find(todo => {
        const endDate = new Date(todo.endDate);
        endDate.setHours(23, 59, 59, 999);
        return endDate.toISOString().split('T')[0] === dateStr;
      });
      
      // ÏãúÏûëÏùºÍ≥º Ï¢ÖÎ£åÏùºÏùÑ Ïó∞Í≤∞ÌïòÎäî ÏÑ† Ïä§ÌÉÄÏùº
      if (startDateTodo && endDateTodo) {
        return 'start-end-todo';  // ÌÅ¥ÎûòÏä§ Ïù¥Î¶ÑÏùÑ ÌÜµÌï¥ Ïä§ÌÉÄÏùº Ï†ÅÏö©
      }
    }
    return null;
  };

  return (
    <div className="calendar-container">
      <header>
        <h2>To-Do List</h2>
        <p>{user.nickname}</p>
        <button onClick={handleLogout}>Logout</button>
      </header>

      <div className="calendar-wrapper">
        <div className="calendar-left">
          <Calendar 
            onChange={handleDateChange} 
            value={date} 
            tileContent={tileContent}  // ÎÇ†Ïßú ÌÉÄÏùºÏóê ÏïÑÏù¥ÏΩò ÌëúÏãú
            tileClassName={tileClassName}  // ÎÇ†Ïßú ÌÉÄÏùºÏóê ÏÑ† Ïä§ÌÉÄÏùº Ï†ÅÏö©
          />
        </div>

        <div className="calendar-right">
          <div className="today-schedule">
            <h3>Ïò§ÎäòÏùò ÏùºÏ†ï</h3>
            {loading ? (
              <p>Î°úÎî© Ï§ë...</p>
            ) : todos.length === 0 ? (
              <p>Ïò§ÎäòÏùò ÏùºÏ†ïÏù¥ ÏóÜÏäµÎãàÎã§.</p>
            ) : (
              <ul>
                {todos.map((todo) => (
                  <li key={todo.todoId} onClick={() => openModal(todo)}>
                    {todo.title}
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/* ÏÑ†ÌÉùÎêú ÎÇ†Ïßú ÏùºÏ†ï */}
          {selectedDateTodos.length > 0 && (
            <div className="selected-date-schedule">
              <h3>{selectedDate}Ïùò ÏùºÏ†ï</h3>
              <ul>
                {selectedDateTodos.map((todo) => (
                  <li key={todo.todoId} onClick={() => openModal(todo)}>
                    {todo.title}
                  </li>
                ))}
              </ul>
            </div>
          )}

          <div className="upcoming-schedule">
            <h3>Îã§Í∞ÄÏò§Îäî ÏùºÏ†ï</h3>
            {loading ? (
              <p>Î°úÎî© Ï§ë...</p>
            ) : upcomingTodos.length === 0 ? (
              <p>Îã§Í∞ÄÏò§Îäî ÏùºÏ†ïÏù¥ ÏóÜÏäµÎãàÎã§.</p>
            ) : (
              <ul>
                {upcomingTodos.slice(0, 3).map((todo) => (
                  <li key={todo.todoId}>
                    {todo.title} - {new Date(todo.startDate).toLocaleDateString()}
                  </li>
                ))}
              </ul>
            )}
          </div>

          <button onClick={() => navigate('/create-todo', { state: { date: selectedDate } })}>
            ÏùºÏ†ï Ï∂îÍ∞Ä
          </button>
        </div>
      </div>

      {/* Î™®Îã¨ Ï∞Ω */}
      {showModal && selectedTodo && (
        <div className="modal">
          <div className="modal-content">
            <h3>{selectedTodo.title}</h3>
            <p><strong>ÏãúÏûëÏùº:</strong> {new Date(selectedTodo.startDate).toLocaleDateString()}</p>
            <p><strong>Ï¢ÖÎ£åÏùº:</strong> {new Date(selectedTodo.endDate).toLocaleDateString()}</p>
            <p><strong>ÎÇ¥Ïö©:</strong> {selectedTodo.description}</p>
            <button onClick={handleEdit}>ÏàòÏ†ïÌïòÍ∏∞</button>
            <button onClick={handleDelete}>ÏÇ≠Ï†úÌïòÍ∏∞</button>
            <button onClick={closeModal}>Îã´Í∏∞</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CalendarComponent;
