import React, { useState, useEffect, useCallback } from 'react';
import Calendar from 'react-calendar';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import 'react-calendar/dist/Calendar.css';
import './calendar.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8888/api';

const CalendarComponent = () => {
  const [date, setDate] = useState(new Date());
  const [allTodos, setAllTodos] = useState([]);
  const [todos, setTodos] = useState([]); // Ïò§ÎäòÏùò ÏùºÏ†ï
  const [selectedDateTodos, setSelectedDateTodos] = useState([]); // ÏÑ†ÌÉùÎêú ÎÇ†ÏßúÏùò ÏùºÏ†ï
  const [upcomingTodos, setUpcomingTodos] = useState([]); // Îã§Í∞ÄÏò§Îäî ÏùºÏ†ï
  const [loading, setLoading] = useState(true);
  const [selectedDate, setSelectedDate] = useState(null); // ÏÑ†ÌÉùÎêú ÎÇ†Ïßú
  const [showModal, setShowModal] = useState(false); // Î™®Îã¨ ÏÉÅÌÉú
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const [selectedTodo, setSelectedTodo] = useState(null); // ÏÑ†ÌÉùÎêú ÏùºÏ†ï
  const navigate = useNavigate();
  const user = JSON.parse(sessionStorage.getItem('user') || '{}');

  // Î°úÍ∑∏Ïù∏ Í≤ÄÏ¶ù
  useEffect(() => {
    if (!user.memId) {
      navigate('/');
    }
  }, [user, navigate]);

  const formatDate = (date) => {
    const d = new Date(date);
    const year = d.getFullYear();
    const month = String(d.getMonth() + 1).padStart(2, '0');
    const day = String(d.getDate()).padStart(2, '0');
    const hours = String(d.getHours()).padStart(2, '0');
    const minutes = String(d.getMinutes()).padStart(2, '0');
    return `${year}-${month}-${day} ${hours}:${minutes}`;
  };

  // Ï†ÑÏ≤¥ ÏùºÏ†ï Í∞ÄÏ†∏Ïò§Í∏∞ Î∞è ÌïÑÌÑ∞ÎßÅ
  const fetchAllTodos = useCallback(async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/todos/mem/${user.memId}`);
      const todosData = response.data;
      setAllTodos(todosData);
      
      // Ïò§ÎäòÏùò ÏùºÏ†ï, Îã§Í∞ÄÏò§Îäî ÏùºÏ†ï, ÏÑ†ÌÉùÎêú ÎÇ†Ïßú ÏùºÏ†ï ÌïÑÌÑ∞ÎßÅ
      const today = new Date().toISOString().split('T')[0];
      const todayTodos = filterTodosByDate(todosData, today);
      setTodos(todayTodos);
      filterUpcomingTodos(todosData);
      
      if (selectedDate) {
        const selectedDateTodos = filterTodosByDate(todosData, selectedDate);
        setSelectedDateTodos(selectedDateTodos);
      } else {
        setSelectedDate(today); // Í∏∞Î≥∏Ï†ÅÏúºÎ°ú Ïò§Îäò ÎÇ†ÏßúÎ°ú ÏÑ§Ï†ï
      }
    } catch (error) {
      alert('ÏùºÏ†ïÏùÑ Í∞ÄÏ†∏Ïò§Îäî Ï§ë Î¨∏Ï†úÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
    } finally {
      setLoading(false);
    }
  }, [user.memId, selectedDate]);

  // ÏùºÏ†ï ÌïÑÌÑ∞ÎßÅ (ÌäπÏ†ï ÎÇ†Ïßú Í∏∞Ï§Ä)
  const filterTodosByDate = (data, date) => {
    const dateObj = new Date(date);
    dateObj.setHours(0, 0, 0, 0);
    return data.filter((todo) => {
      const startDate = new Date(todo.startDate);
      const endDate = new Date(todo.endDate);
      startDate.setHours(0, 0, 0, 0);
      endDate.setHours(23, 59, 59, 999);
      return startDate <= dateObj && endDate >= dateObj;
    });
  };

  // Îã§Í∞ÄÏò§Îäî ÏùºÏ†ï ÌïÑÌÑ∞ÎßÅ
  const filterUpcomingTodos = (data) => {
    const upcoming = data.filter((todo) => new Date(todo.startDate) > new Date());
    setUpcomingTodos(upcoming.sort((a, b) => new Date(a.startDate) - new Date(b.startDate)));
  };

  // ÎÇ†Ïßú ÏÑ†ÌÉù Ìï∏Îì§Îü¨
  const handleDateChange = (date) => {
    const fixedDate = new Date(date);
    fixedDate.setHours(fixedDate.getHours() + 9); // ÌïúÍµ≠ ÏãúÍ∞ÑÏúºÎ°ú ÎßûÏ∂§
    setDate(fixedDate);
    const isoDate = fixedDate.toISOString().split('T')[0]; 
    setSelectedDate(isoDate);
  };

  // ÏôÑÎ£å Ï≤òÎ¶¨
  const handleMarkAsComplete = async () => {
    if (!selectedTodo) return;
    try {
      await axios.patch(`${API_BASE_URL}/todos/${selectedTodo.todoId}`, {
        completeYn: true
      });
      // ÏôÑÎ£å Ï≤òÎ¶¨ ÌõÑ ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
      setAllTodos(allTodos.map(todo => 
        todo.todoId === selectedTodo.todoId ? { ...todo, completeYn: 1 } : todo
      ));
      closeModal();
    } catch (error) {
      alert('ÏôÑÎ£å Ï≤òÎ¶¨Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  // ÌéòÏù¥ÏßÄ Î°úÎìú Ïãú ÏùºÏ†ï Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    fetchAllTodos();
  }, [fetchAllTodos]);

  // Î™®Îã¨ Îã´Í∏∞
  const closeModal = () => {
    setShowModal(false);
    setSelectedTodo(null);
  };

  // ÏùºÏ†ï ÌÅ¥Î¶≠ Ïãú Î™®Îã¨ Ïó¥Í∏∞
  const openModal = (todo) => {
    setSelectedTodo(todo);
    setShowModal(true);
  };

  // ÏÇ≠Ï†ú ÌôïÏù∏ Î™®Îã¨ Ïó¥Í∏∞
  const openDeleteConfirm = () => {
    setShowModal(false);
    setShowDeleteConfirm(true);
  };

  // ÏÇ≠Ï†ú ÌôïÏù∏ Î™®Îã¨ Îã´Í∏∞
  const closeDeleteConfirm = () => {
    setShowDeleteConfirm(false);
    setShowModal(true);
  };

  // ÏùºÏ†ï ÏÇ≠Ï†ú
  const handleDelete = async () => {
    if (!selectedTodo) return;
    try {
      await axios.delete(`${API_BASE_URL}/todos/${selectedTodo.todoId}`);
      setAllTodos(allTodos.filter(todo => todo.todoId !== selectedTodo.todoId));
      closeModal();
    } catch (error) {
      alert('ÏùºÏ†ï ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  // ÏùºÏ†ï ÏàòÏ†ï
  const handleEdit = () => {
    navigate('/edit-todo', { state: { todoId: selectedTodo.todoId } }); // todoId Ï†ÑÎã¨
    closeModal();
  };

  const handleLogout = () => {
    sessionStorage.removeItem('user');
    navigate('/');
  };

  const tileContent = ({ date, view }) => {
    if (view === 'month') {
      const dateStr = date.toISOString().split('T')[0]; // ÎÇ†Ïßú ÌòïÏãù: YYYY-MM-DD
  
      // Ìï¥Îãπ ÎÇ†ÏßúÏóê ÏùºÏ†ïÏù¥ ÏûàÎäîÏßÄ ÌôïÏù∏
      const hasTodo = allTodos.some(todo => {
        const startDate = new Date(todo.startDate);
        const endDate = new Date(todo.endDate);
        startDate.setHours(0, 0, 0, 0);
        endDate.setHours(23, 59, 59, 999);
  
        // ÎÇ†Ïßú ÎπÑÍµê: ÏùºÏ†ïÏùò ÏãúÏûëÏùºÏù¥ ÌòÑÏû¨ ÎÇ†ÏßúÎ≥¥Îã§ Í∞ôÍ±∞ÎÇò Ïù¥Ï†Ñ, Ï¢ÖÎ£åÏùºÏù¥ ÌòÑÏû¨ ÎÇ†ÏßúÎ≥¥Îã§ Ïù¥Ï†ÑÏù¥Î©¥ ÌëúÏãúÌïòÏßÄ ÏïäÏùå
        return (
          (startDate <= date && endDate >= date) ||  // ÏãúÏûëÏùºÍ≥º Ï¢ÖÎ£åÏùºÏù¥ Ìï¥Îãπ ÎÇ†ÏßúÏóê Ìè¨Ìï®ÎêòÎäî Í≤ΩÏö∞
          (startDate.toISOString().split('T')[0] === dateStr) ||  // ÏãúÏûëÏùºÏù¥ Ìï¥Îãπ ÎÇ†ÏßúÏôÄ ÎèôÏùºÌïú Í≤ΩÏö∞
          (endDate.toISOString().split('T')[0] === dateStr && date < endDate) // Ï¢ÖÎ£åÏùºÏù¥ Ìï¥Îãπ ÎÇ†ÏßúÏôÄ ÎèôÏùºÌïòÏßÄÎßå, Ï¢ÖÎ£åÏùº Ïù¥ÌõÑÎäî Ìè¨Ìï®ÎêòÏßÄ ÏïäÏùå
        );
      });
  
      return hasTodo ? <span className="todo-icon">üíï</span> : null;
    }
    return null;
  };

  return (
    <div className="calendar-container">
      <header>
        <h2>TODO LIST</h2>
        <p>
          <span className="nickname">{user.nickname}</span> Îãò ÌôòÏòÅÌï©ÎãàÎã§!
        </p>
        <button className='logoutBtn' onClick={handleLogout}>LOGOUT</button>
      </header>

      <div className="calendar-wrapper">
        <div className="calendar-left">
          <Calendar 
            onChange={handleDateChange} 
            value={date} 
            tileContent={tileContent}
            tileClassName={tileClassName}
          />
        </div>

        <div className="calendar-right">
          <div className="today-schedule">
            <h3>Ïò§ÎäòÏùò ÏùºÏ†ï</h3>
            {loading ? (
              <p>Î°úÎî© Ï§ë...</p>
            ) : todos.length === 0 ? (
              <p>Ïò§ÎäòÏùò ÏùºÏ†ïÏù¥ ÏóÜÏäµÎãàÎã§.</p>
            ) : (
              <ul>
                {todos.map((todo) => (
                  <li key={todo.todoId} onClick={() => openModal(todo)}>{todo.title}</li>
                ))}
              </ul>
            )}
          </div>

          {selectedDateTodos.length > 0 && (
            <div className="selected-date-schedule">
              <h3>{selectedDate}Ïùò ÏùºÏ†ï</h3>
              <ul>
                {selectedDateTodos.map((todo) => (
                  <li key={todo.todoId} onClick={() => openModal(todo)}>{todo.title}</li>
                ))}
              </ul>
            </div>
          )}

          <div className="upcoming-schedule">
            <h3>Îã§Í∞ÄÏò§Îäî ÏùºÏ†ï</h3>
            {loading ? (
              <p>Î°úÎî© Ï§ë...</p>
            ) : upcomingTodos.length === 0 ? (
              <p>Îã§Í∞ÄÏò§Îäî ÏùºÏ†ïÏù¥ ÏóÜÏäµÎãàÎã§.</p>
            ) : (
              <ul>
                {upcomingTodos.slice(0, 3).map((todo) => (
                  <li key={todo.todoId} onClick={() => openModal(todo)}>
                    {todo.title} - {new Date(todo.startDate).toLocaleDateString()}
                  </li>
                ))}
              </ul>
            )}
          </div>

          <button onClick={() => navigate('/create-todo', { state: { date: selectedDate } })}>
            ÏùºÏ†ï Ï∂îÍ∞Ä
          </button>
        </div>
      </div>

      {/* ÏÇ≠Ï†ú ÌôïÏù∏ Î™®Îã¨ */}
      {showDeleteConfirm && (
        <div className="modal">
          <div className="delete-modal-content">
            <h3>Ï†ïÎßê ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?</h3>
            <button onClick={handleDelete}>ÌôïÏù∏</button>
            <button onClick={closeDeleteConfirm}>Ï∑®ÏÜå</button>
          </div>
        </div>
      )}

      {showModal && selectedTodo && (
        <div className="modal">
          <div className="modal-content">
            <span className="close-btn" onClick={closeModal}>√ó</span>
            <h2>{selectedTodo.title}</h2>
            <h4>{selectedTodo.content}</h4>
            <p>ÏãúÏûëÏùº: {formatDate(selectedTodo.startDate)}</p>
            <p>Ï¢ÖÎ£åÏùº: {formatDate(selectedTodo.endDate)}</p>
            <p>ÏôÑÎ£å Ïó¨Î∂Ä: {selectedTodo.completeYn ? 'ÏôÑÎ£å' : 'ÏßÑÌñâ Ï§ë'}</p>
              {selectedTodo.completeYn === false && (
                <button onClick={handleMarkAsComplete}>ÏôÑÎ£å Ï≤òÎ¶¨ÌïòÍ∏∞</button>
              )}
              {selectedTodo.completeYn === true && (
                <button onClick={handleMarkAsComplete}>ÎØ∏ÏôÑÎ£å</button>
              )}
            <button onClick={handleEdit}>ÏàòÏ†ï</button>
            <button onClick={openDeleteConfirm}>ÏÇ≠Ï†ú</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CalendarComponent;
