import React, { useState } from 'react';
import { GoogleLogin } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const LoginPage = () => {
  const [nickname, setNickname] = useState('');
  const navigate = useNavigate();

  const handleGoogleSuccess = async (response) => {
    const token = response.credential;
    try {
      const res = await axios.post('http://localhost:8888/api/auth/google', { token });
      if (res.data.user) {
        navigate('/nickname'); // 로그인 성공 후 닉네임 입력 페이지로 이동
      }
    } catch (error) {
      console.error('Google login error:', error);
    }
  };

  const handleNicknameSubmit = async () => {
    try {
      await axios.post('http://localhost:8888/api/users', { nickname });
      navigate('/calendar'); // 닉네임 저장 후 캘린더 페이지로 이동
    } catch (error) {
      console.error('Nickname submit error:', error);
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <GoogleLogin onSuccess={handleGoogleSuccess} onError={() => console.log('Login Failed')} />
      <div>
        <input
          type="text"
          placeholder="Enter your nickname"
          value={nickname}
          onChange={(e) => setNickname(e.target.value)}
        />
        <button onClick={handleNicknameSubmit}>Submit</button>
      </div>
    </div>
  );
};

export default LoginPage;
