import React, { useState, useEffect } from 'react';
import { GoogleLogin } from '@react-oauth/google';
import KakaoLogin from 'react-kakao-login'; // 카카오 로그인 컴포넌트
import axios from 'axios'; // axios 임포트

const LoginPage = () => {
  const [isNicknameModalOpen, setNicknameModalOpen] = useState(false); // 닉네임 입력창 상태
  const [userInfo, setUserInfo] = useState(null); // 로그인 후 사용자 정보 저장
  const [nickname, setNickname] = useState(''); // 사용자 닉네임
  const token = response.credential;
  // Google 로그인 성공 시 호출되는 함수
  const handleGoogleLoginSuccess = async (response) => {
    console.log('Google Login Success:', response);

    // 구글 토큰을 URL-safe base64에서 일반 base64로 변환
    const base64Url = response.credential.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    const decoded = JSON.parse(atob(base64)); // 구글 토큰 디코딩
    const email = decoded.email;  // 이메일
    const nickname = decoded.given_name; // 이름

    // 구글 로그인 후 사용자 정보를 상태에 저장
    setUserInfo({ email, nickname, provider: 'google' });
    setNicknameModalOpen(true); // 닉네임 입력 창 띄우기
  };

  // Google 로그인 실패 시 호출되는 함수
  const handleGoogleLoginFailure = (error) => {
    console.error('Google Login Failed:', error);
    alert('Google 로그인에 실패했습니다. 다시 시도해주세요.');
  };

  // Kakao 로그인 성공 시 호출되는 함수
  const handleKakaoLoginSuccess = async (response) => {
    console.log('Kakao Login Success:', response);

    const email = response.response.email; // 이메일
    const nickname = response.response.properties.nickname; // 닉네임

    // 카카오 로그인 후 사용자 정보를 상태에 저장
    setUserInfo({ email, nickname, provider: 'kakao' });
    setNicknameModalOpen(true); // 닉네임 입력 창 띄우기
  };

  // Kakao 로그인 실패 시 호출되는 함수
  const handleKakaoLoginFailure = (error) => {
    console.error('Kakao Login Failed:', error);
    alert('Kakao 로그인에 실패했습니다. 다시 시도해주세요.');
  };

  // 닉네임 중복 검사 및 사용자 정보 저장
  const handleNicknameSubmit = async () => {
    try {
      // 닉네임 중복 검사
      const result = await axios.post('http://localhost:8888/api/login/check-nickname', { nickname });

      if (result.data.exists) {
        alert('이미 사용 중인 닉네임입니다. 다른 닉네임을 선택하세요.');
      } else {
        // 중복되지 않으면 사용자 정보 저장
        const userData = { ...userInfo, nickname };

        if (userInfo.provider === 'google') {
          await axios.post('http://localhost:8888/api/login/google', userData); // 구글 로그인 후 저장
        } else if (userInfo.provider === 'kakao') {
          await axios.post('http://localhost:8888/api/login/kakao', userData); // 카카오 로그인 후 저장
        }

        alert('회원가입이 완료되었습니다.');
        setNicknameModalOpen(false); // 닉네임 입력창 닫기
      }
    } catch (error) {
      console.error('Error saving nickname:', error);
      alert('회원가입에 실패했습니다. 다시 시도해주세요.');
    }
  };

  // 닉네임 입력 변경 핸들러
  const handleNicknameChange = (event) => {
    setNickname(event.target.value);
  };

  // 카카오 SDK 초기화
  useEffect(() => {
    if (window.Kakao) {
      window.Kakao.init('ae86decbc5071f303518b916484a7076'); // 카카오 REST API 키
    }
  }, []);

  return (
    <div className="login-container">
      <h2>Login Page</h2>

      {/* Google 로그인 버튼 */}
      <GoogleLogin
        onSuccess={handleGoogleLoginSuccess}
        onError={handleGoogleLoginFailure}
        theme="outline" // 버튼 스타일 설정 (예: outline)
      />

      {/* Kakao 로그인 버튼 */}
      <KakaoLogin
        jsKey="ae86decbc5071f303518b916484a7076" // 카카오 REST API 키
        onSuccess={handleKakaoLoginSuccess}
        onFailure={handleKakaoLoginFailure}
        buttonText="Kakao Login" // 버튼 텍스트 설정
      />

      {/* 닉네임 입력창 */}
      {isNicknameModalOpen && (
        <div className="nickname-modal">
          <h3>닉네임을 입력해주세요</h3>
          <input
            type="text"
            value={nickname}
            onChange={handleNicknameChange}
            placeholder="닉네임"
          />
          <button onClick={handleNicknameSubmit}>확인</button>
          <button onClick={() => setNicknameModalOpen(false)}>취소</button>
        </div>
      )}
    </div>
  );
};

export default LoginPage;
