import React, { useState, useEffect, useCallback } from 'react';
import Calendar from 'react-calendar';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import 'react-calendar/dist/Calendar.css'; 
import './calendar.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8888/api';

const CalendarComponent = () => {
  const [date, setDate] = useState(new Date()); // 기본값은 현재 날짜
  const [allTodos, setAllTodos] = useState([]); // 전체 일정 상태
  const [todos, setTodos] = useState([]); // 선택한 날짜의 일정
  const [upcomingTodos, setUpcomingTodos] = useState([]); // 다가오는 일정
  const [loading, setLoading] = useState(true); // 로딩 상태
  const [selectedDate, setSelectedDate] = useState(null); // 선택된 날짜
  const navigate = useNavigate();
  const user = JSON.parse(sessionStorage.getItem('user') || '{}');

  // 로그인 검증
  useEffect(() => {
    if (!user.memId) {
      navigate('/');
    }
  }, [user, navigate]);

  // 전체 일정 가져오기 (API 중복 호출 방지)
  const fetchAllTodos = useCallback(async () => {
    if (allTodos.length > 0) return; // 이미 데이터가 있으면 호출 방지
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/todos/mem/${user.memId}`);
      const todosData = response.data;
      setAllTodos(todosData);
      filterUpcomingTodos(todosData);
    } catch (error) {
      alert('일정을 가져오는 중 문제가 발생했습니다. 다시 시도해주세요.');
    } finally {
      setLoading(false);
    }
  }, [allTodos.length, user.memId]);

  // 다가오는 일정 필터링
  const filterUpcomingTodos = (data) => {
    const upcoming = data.filter((todo) => new Date(todo.startDate) > new Date());
    setUpcomingTodos(upcoming.sort((a, b) => new Date(a.startDate) - new Date(b.startDate)));
  };

  // 선택된 날짜의 일정 필터링
  const filterTodosByDate = (selectedDate) => {
    const selectedDateObj = new Date(selectedDate);
    selectedDateObj.setHours(0, 0, 0, 0); // 시간은 00:00:00으로 설정

    const filtered = allTodos.filter((todo) => {
      const startDate = new Date(todo.startDate);
      const endDate = new Date(todo.endDate);

      startDate.setHours(0, 0, 0, 0); // 시간은 00:00:00으로 설정
      endDate.setHours(23, 59, 59, 999); // 종료 날짜의 시간은 23:59:59로 설정

      // 선택된 날짜가 시작일과 종료일 범위 내에 있는지 확인
      return selectedDateObj >= startDate && selectedDateObj <= endDate;
    });

    setTodos(filtered);
  };

  // 날짜 선택 핸들러
  const handleDateChange = (date) => {
    const fixedDate = new Date(date);
    fixedDate.setHours(fixedDate.getHours() + 9); // 한국시간으로 맞추기 위해 9시간 더하기
    fixedDate.setMinutes(0);
    fixedDate.setSeconds(0);
    fixedDate.setMilliseconds(0);

    setDate(fixedDate);
    const isoDate = fixedDate.toISOString().split('T')[0];  // ISO 형식으로 변환
    setSelectedDate(isoDate);
    filterTodosByDate(isoDate);  // 선택한 날짜에 대한 일정만 필터링
  };

  // 선택된 날짜가 없으면 오늘 날짜를 기본으로 설정
  useEffect(() => {
    if (!selectedDate) {
      const today = new Date().toISOString().split('T')[0]; // 오늘 날짜 설정
      setSelectedDate(today);
      filterTodosByDate(today);  // 오늘 날짜의 일정만 필터링
    }
  }, [selectedDate]);

  useEffect(() => {
    fetchAllTodos();
  }, [fetchAllTodos]);

  const handleLogout = () => {
    sessionStorage.removeItem('user');
    navigate('/');
  };

  return (
    <div className="calendar-container">
      <header>
        <h2>To-Do List</h2>
        <p>{user.nickname}</p>
        <button onClick={handleLogout}>Logout</button>
      </header>

      <div className="calendar-wrapper">
        <div className="calendar-left">
          <Calendar onChange={handleDateChange} value={date} />
        </div>

        <div className="calendar-right">
          <div className="today-schedule">
            <h3>오늘의 일정</h3>
            {loading ? (
              <p>로딩 중...</p>
            ) : todos.length === 0 ? (
              <p>오늘의 일정이 없습니다.</p>
            ) : (
              <ul>
                {todos.map((todo) => (
                  <li key={todo.todoId}>{todo.title}</li>
                ))}
              </ul>
            )}
          </div>

          <div className="upcoming-schedule">
            <h3>다가오는 일정</h3>
            {loading ? (
              <p>로딩 중...</p>
            ) : upcomingTodos.length === 0 ? (
              <p>다가오는 일정이 없습니다.</p>
            ) : (
              <ul>
                {upcomingTodos.slice(0, 3).map((todo) => (
                  <li key={todo.todoId}>
                    {todo.title} - {new Date(todo.startDate).toLocaleDateString()}
                  </li>
                ))}
              </ul>
            )}
          </div>

          <button onClick={() => navigate('/create-todo', { state: { date: selectedDate } })}>
            일정 추가
          </button>
        </div>
      </div>
    </div>
  );
};

export default CalendarComponent;
