import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './calendar.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8888/api';

const Popup = ({ message, onConfirm, onClose }) => (
  <div className="popup">
    <div className="popup-content">
      <p>{message}</p>
      <button onClick={onConfirm}>예</button>
      <button onClick={onClose}>아니요</button>
    </div>
  </div>
);

const CalendarComponent = () => {
  const [date, setDate] = useState(new Date());
  const [todos, setTodos] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const user = JSON.parse(sessionStorage.getItem('user') || '{}');

  useEffect(() => {
    if (!user) {
      navigate('/');
    }
  }, [user, navigate]);

  const handleLogout = () => {
    sessionStorage.removeItem('user');
    navigate('/');
  };

  const fetchTodos = async (selectedDate) => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/todos`, {
        params: { date: selectedDate }
      });
      setTodos(response.data);
    } catch (error) {
      alert('일정을 가져오는 중 문제가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  const handleDateChange = (date) => {
    setDate(date);
    setSelectedDate(date);
  };

  const handlePopupClose = () => {
    setShowPopup(false);
  };

  const handlePopupConfirm = () => {
    navigate('/create-todo', { state: { date: selectedDate } });
    setShowPopup(false);
  };

  useEffect(() => {
    fetchTodos(date.toISOString().split('T')[0]);
  }, [date]);

  return (
    <div className="calendar-container">
      <header>
        <h2>To-Do List</h2>
        <p>{user.nickname}</p>
        <button onClick={handleLogout}>Logout</button>
      </header>

      <div className="calendar-wrapper">
        <div className="calendar-left">
          <Calendar onChange={handleDateChange} value={date} />
        </div>

        <div className="calendar-right">
          <div className="today-schedule">
            <h3>오늘의 일정</h3>
            {loading ? (
              <p>Loading...</p>
            ) : (
              <ul>
                {todos.map((todo) => (
                  <li key={todo.id}>{todo.title}</li>
                ))}
              </ul>
            )}
          </div>
          <button onClick={() => navigate('/create-todo', { state: { date: selectedDate } })}>
            일정 추가
          </button>
        </div>
      </div>

      {showPopup && (
        <Popup
          message="일정을 추가하시겠습니까?"
          onConfirm={handlePopupConfirm}
          onClose={handlePopupClose}
        />
      )}
    </div>
  );
};

export default CalendarComponent;
