import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import 'react-calendar/dist/Calendar.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8888/api';

const Popup = ({ message, onConfirm, onClose }) => (
  <div className="popup">
    <div className="popup-content">
      <p>{message}</p>
      <button onClick={onConfirm}>예</button>
      <button onClick={onClose}>아니요</button>
    </div>
  </div>
);

const CalendarComponent = () => {
  const [date, setDate] = useState(new Date());
  const [todos, setTodos] = useState([]);
  const [selectedTodo, setSelectedTodo] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [newTodo, setNewTodo] = useState('');
  const [showPopup, setShowPopup] = useState(false);
  const [selectedDate, setSelectedDate] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const user = JSON.parse(sessionStorage.getItem('user') || '{}');
  console.log('User:', user); // 이 부분으로 user 객체를 확인


  useEffect(() => {
    if (!user) {
      navigate('/');
    }
  }, [user, navigate]);

  const handleLogout = () => {
    sessionStorage.removeItem('user');
    navigate('/');
  };

  const fetchTodos = async (selectedDate) => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/todos`, {
        params: { date: selectedDate }
      });
      setTodos(response.data);
    } catch (error) {
      alert('일정을 가져오는 중 문제가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  const handleDateChange = (date) => {
    setDate(date);
    setSelectedDate(date);
    setShowPopup(true);
  };

  const handleAddTodo = async () => {
    try {
      await axios.post(`${API_BASE_URL}/todos`, { date: selectedDate, title: newTodo });
      setNewTodo('');
      fetchTodos(selectedDate.toISOString().split('T')[0]);
    } catch (error) {
      alert('일정을 추가하는 중 문제가 발생했습니다.');
    }
  };

  const handleDeleteTodo = async (todoId) => {
    try {
      await axios.delete(`${API_BASE_URL}/todos/${todoId}`);
      fetchTodos(date.toISOString().split('T')[0]);
    } catch (error) {
      alert('일정을 삭제하는 중 문제가 발생했습니다.');
    }
  };

  const handleEditTodo = (todo) => {
    setSelectedTodo(todo);
    setIsEditing(true);
  };

  const handleSaveEdit = async () => {
    try {
      await axios.put(`${API_BASE_URL}/todos/${selectedTodo.id}`, { title: selectedTodo.title });
      fetchTodos(date.toISOString().split('T')[0]);
      setIsEditing(false);
      setSelectedTodo(null);
    } catch (error) {
      alert('일정을 수정하는 중 문제가 발생했습니다.');
    }
  };

  const handlePopupClose = () => {
    setShowPopup(false);
  };

  const handlePopupConfirm = () => {
    navigate('/create-todo', { state: { date: selectedDate } });
    setShowPopup(false);
  };

  useEffect(() => {
    fetchTodos(date.toISOString().split('T')[0]);
  }, [date]);

  return (
    <div className="calendar-container">
      <header>
        <h2>To-Do List</h2>
        <p>${user.nickname}</p>
        <button onClick={handleLogout}>Logout</button>
      </header>

      <Calendar onChange={handleDateChange} value={date} />

      {showPopup && (
        <Popup
          message="일정을 추가하시겠습니까?"
          onConfirm={handlePopupConfirm}
          onClose={handlePopupClose}
        />
      )}

      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="todo-list">
          {isEditing ? (
            <div>
              <input
                type="text"
                value={selectedTodo.title}
                onChange={(e) => setSelectedTodo({ ...selectedTodo, title: e.target.value })}
              />
              <button onClick={handleSaveEdit}>Save</button>
            </div>
          ) : (
            <div className="add-todo">
              <input
                type="text"
                value={newTodo}
                onChange={(e) => setNewTodo(e.target.value)}
                placeholder="New task"
              />
              <button onClick={handleAddTodo}>Add</button>
            </div>
          )}

          <ul>
            {todos.map((todo) => (
              <li key={todo.id}>
                {todo.title}
                <button onClick={() => handleEditTodo(todo)}>Edit</button>
                <button onClick={() => handleDeleteTodo(todo.id)}>Delete</button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default CalendarComponent;
