import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import 'react-calendar/dist/Calendar.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8888/api';

const CalendarComponent = () => {
  const [date, setDate] = useState(new Date());
  const [todos, setTodos] = useState([]);
  const [selectedTodo, setSelectedTodo] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [newTodo, setNewTodo] = useState('');
  const [showPopup, setShowPopup] = useState(false); // 팝업 상태
  const [selectedDate, setSelectedDate] = useState(null); // 선택된 날짜
  const [loading, setLoading] = useState(false); // 로딩 상태
  const navigate = useNavigate();
  const user = JSON.parse(sessionStorage.getItem('user'));

  const handleLogout = () => {
    sessionStorage.removeItem('user'); // 로그아웃 시 세션 삭제
    navigate('/login'); // 로그인 페이지로 리다이렉트
  };
  
  const fetchTodos = async (selectedDate) => {
    setLoading(true); // 로딩 시작
    try {
      const response = await axios.get('http://localhost:8888/api/todos', {
        params: { date: selectedDate }
      });
      setTodos(response.data);
    } catch (error) {
      console.error('Error fetching todos:', error);
    } finally {
      setLoading(false); // 로딩 끝
    }
  };

  const handleDateChange = (date) => {
    setDate(date);
    setSelectedDate(date);
    setShowPopup(true); // 팝업 띄우기
  };

  const handleAddTodo = () => {
    axios.post('http://localhost:8888/api/todos', { date, title: newTodo })
      .then(response => {
        fetchTodos(date.toISOString().split('T')[0]); // 새로 고침
        setNewTodo('');
      })
      .catch(error => console.error('Error adding todo:', error));
  };

  const handleDeleteTodo = (todoId) => {
    axios.delete(`http://localhost:8888/api/todos/${todoId}`)
      .then(() => {
        fetchTodos(date.toISOString().split('T')[0]); // 새로 고침
      })
      .catch(error => console.error('Error deleting todo:', error));
  };

  const handleEditTodo = (todo) => {
    setSelectedTodo(todo);
    setIsEditing(true);
  };

  const handleSaveEdit = () => {
    axios.put(`http://localhost:8888/api/todos/${selectedTodo.id}`, { title: selectedTodo.title })
      .then(() => {
        fetchTodos(date.toISOString().split('T')[0]); // 새로 고침
        setIsEditing(false);
        setSelectedTodo(null);
      })
      .catch(error => console.error('Error editing todo:', error));
  };

  const handlePopupClose = () => {
    setShowPopup(false);
  };

  const handlePopupConfirm = () => {
    navigate('/create-todo', { state: { date: selectedDate } }); // 일정 작성 페이지로 이동
    setShowPopup(false);
  };

  useEffect(() => {
    fetchTodos(date.toISOString().split('T')[0]);
  }, [date]);

  return (
    <div>
      <h2>To-Do List</h2>
      <Calendar onChange={handleDateChange} value={date} />
      
      {/* 팝업 */}
      {showPopup && (
        <div className="popup">
          <p>일정을 추가하시겠습니까?</p>
          <button onClick={handlePopupConfirm}>예</button>
          <button onClick={handlePopupClose}>아니요</button>
        </div>
      )}

      {loading ? (
        <p>Loading...</p> // 로딩 상태 표시
      ) : (
        <div>
          {isEditing ? (
            <div>
              <input
                type="text"
                value={selectedTodo.title}
                onChange={(e) => setSelectedTodo({ ...selectedTodo, title: e.target.value })}
              />
              <button onClick={handleSaveEdit}>Save</button>
            </div>
          ) : (
            <div>
              <input
                type="text"
                value={newTodo}
                onChange={(e) => setNewTodo(e.target.value)}
                placeholder="New task"
              />
              <button onClick={handleAddTodo}>Add</button>
            </div>
          )}
        </div>
      )}

      <ul>
        {todos.map(todo => (
          <li key={todo.id}>
            {todo.title}
            <button onClick={() => handleEditTodo(todo)}>Edit</button>
            <button onClick={() => handleDeleteTodo(todo.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CalendarComponent;
