import React, { useState, useEffect, useCallback } from 'react';
import Calendar from 'react-calendar';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import 'react-calendar/dist/Calendar.css';
import './calendar.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8888/api';

const CalendarComponent = () => {
  const [date, setDate] = useState(new Date());
  const [allTodos, setAllTodos] = useState([]);
  const [todos, setTodos] = useState([]);  // 오늘의 일정
  const [selectedDateTodos, setSelectedDateTodos] = useState([]);  // 선택된 날짜의 일정
  const [loading, setLoading] = useState(true);
  const [selectedDate, setSelectedDate] = useState(null);
  const [showModal, setShowModal] = useState(false);  // 모달 상태
  const [selectedTodo, setSelectedTodo] = useState(null);  // 선택된 일정
  const navigate = useNavigate();
  const user = JSON.parse(sessionStorage.getItem('user') || '{}');

  // 로그인 검증
  useEffect(() => {
    if (!user.memId) {
      navigate('/');
    }
  }, [user, navigate]);

  // 전체 일정 가져오기
  const fetchAllTodos = useCallback(async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/todos/mem/${user.memId}`);
      const todosData = response.data;
      setAllTodos(todosData);
      filterTodosByToday(todosData);  // 오늘의 일정 필터링
      filterUpcomingTodos(todosData);  // 다가오는 일정 필터링
      if (selectedDate) {
        filterTodosByDate(selectedDate, todosData);  // 선택된 날짜에 맞는 일정 필터링
      } else {
        // 선택된 날짜가 없다면, 기본적으로 오늘 날짜로 설정
        setSelectedDate(new Date().toISOString().split('T')[0]);
      }
    } catch (error) {
      alert('일정을 가져오는 중 문제가 발생했습니다. 다시 시도해주세요.');
    } finally {
      setLoading(false);
    }
  }, [user.memId, selectedDate]);

  // 오늘의 일정 필터링
  const filterTodosByToday = (data) => {
    const today = new Date().toISOString().split('T')[0]; // 오늘 날짜
    const filtered = data.filter((todo) => {
      const startDate = new Date(todo.startDate);
      const endDate = new Date(todo.endDate);
      startDate.setHours(0, 0, 0, 0);
      endDate.setHours(23, 59, 59, 999);
      return today >= startDate.toISOString().split('T')[0] && today <= endDate.toISOString().split('T')[0];
    });
    setTodos(filtered);
  };

  // 선택된 날짜의 일정 필터링
  const filterTodosByDate = (selectedDate, data) => {
    const selectedDateObj = new Date(selectedDate);
    selectedDateObj.setHours(0, 0, 0, 0);
    const filtered = data.filter((todo) => {
      const startDate = new Date(todo.startDate);
      const endDate = new Date(todo.endDate);
      startDate.setHours(0, 0, 0, 0);
      endDate.setHours(23, 59, 59, 999);
      return selectedDateObj >= startDate && selectedDateObj <= endDate;
    });
    setSelectedDateTodos(filtered);
  };

  // 다가오는 일정 필터링
  const filterUpcomingTodos = (data) => {
    const upcoming = data.filter((todo) => new Date(todo.startDate) > new Date());
    setUpcomingTodos(upcoming.sort((a, b) => new Date(a.startDate) - new Date(b.startDate)));
  };

  // 날짜 선택 핸들러
  const handleDateChange = (date) => {
    const fixedDate = new Date(date);
    fixedDate.setHours(fixedDate.getHours() + 9); 
    fixedDate.setMinutes(0);
    fixedDate.setSeconds(0);
    fixedDate.setMilliseconds(0);
    setDate(fixedDate);
    const isoDate = fixedDate.toISOString().split('T')[0]; 
    setSelectedDate(isoDate);
  };

  // 페이지 로드 시 오늘 날짜 일정 가져오기
  useEffect(() => {
    fetchAllTodos();
  }, [fetchAllTodos]);

  useEffect(() => {
    if (selectedDate && allTodos.length > 0) {
      filterTodosByDate(selectedDate, allTodos);
    }
  }, [selectedDate, allTodos]);

  // 모달 닫기
  const closeModal = () => {
    setShowModal(false);
    setSelectedTodo(null);
  };

  // 일정 클릭 시 모달 열기
  const openModal = (todo) => {
    setSelectedTodo(todo);
    setShowModal(true);
  };

  // 일정 삭제
  const handleDelete = async () => {
    if (!selectedTodo) return;
    try {
      await axios.delete(`${API_BASE_URL}/todos/${selectedTodo.todoId}`);
      setAllTodos(allTodos.filter(todo => todo.todoId !== selectedTodo.todoId));
      closeModal();
    } catch (error) {
      alert('일정 삭제에 실패했습니다.');
    }
  };

  // 일정 수정
  const handleEdit = () => {
    // 수정 페이지로 이동
    navigate('/edit-todo', { state: { todo: selectedTodo } });
    closeModal();
  };

  const handleLogout = () => {
    sessionStorage.removeItem('user');
    navigate('/');
  };

  // Calendar tileContent를 사용하여 날짜에 아이콘 표시
  const tileContent = ({ date, view }) => {
    if (view === 'month') {
      const dateStr = date.toISOString().split('T')[0]; // 날짜 형식: YYYY-MM-DD
      
      // 해당 날짜에 일정이 있는지 확인
      const hasTodo = allTodos.some(todo => {
        const startDate = new Date(todo.startDate);
        const endDate = new Date(todo.endDate);
        startDate.setHours(0, 0, 0, 0);
        endDate.setHours(23, 59, 59, 999);
        
        // 날짜 비교: 일정의 시작일 또는 종료일이 선택된 날짜와 일치하는지 확인
        return startDate.toISOString().split('T')[0] === dateStr || endDate.toISOString().split('T')[0] === dateStr;
      });
      
      return hasTodo ? <span className="todo-icon">📅</span> : null;
    }
    return null;
  };

  // Calendar tileClassName을 사용하여 시작일과 종료일을 선으로 연결
  const tileClassName = ({ date, view }) => {
    if (view === 'month') {
      const dateStr = date.toISOString().split('T')[0]; // 날짜 형식: YYYY-MM-DD
      
      // 일정의 시작일과 종료일을 확인하여 선을 연결
      const startDateTodo = allTodos.find(todo => {
        const startDate = new Date(todo.startDate);
        startDate.setHours(0, 0, 0, 0);
        return startDate.toISOString().split('T')[0] === dateStr;
      });
      
      const endDateTodo = allTodos.find(todo => {
        const endDate = new Date(todo.endDate);
        endDate.setHours(23, 59, 59, 999);
        return endDate.toISOString().split('T')[0] === dateStr;
      });
      
      // 시작일과 종료일을 연결하는 선 스타일
      if (startDateTodo && endDateTodo) {
        return 'start-end-todo';  // 클래스 이름을 통해 스타일 적용
      }
    }
    return null;
  };

  return (
    <div className="calendar-container">
      <header>
        <h2>To-Do List</h2>
        <p>{user.nickname}</p>
        <button onClick={handleLogout}>Logout</button>
      </header>

      <div className="calendar-wrapper">
        <div className="calendar-left">
          <Calendar 
            onChange={handleDateChange} 
            value={date} 
            tileContent={tileContent}  // 날짜 타일에 아이콘 표시
            tileClassName={tileClassName}  // 날짜 타일에 선 스타일 적용
          />
        </div>

        <div className="calendar-right">
          <div className="today-schedule">
            <h3>오늘의 일정</h3>
            {loading ? (
              <p>로딩 중...</p>
            ) : todos.length === 0 ? (
              <p>오늘의 일정이 없습니다.</p>
            ) : (
              <ul>
                {todos.map((todo) => (
                  <li key={todo.todoId} onClick={() => openModal(todo)}>
                    {todo.title}
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/* 선택된 날짜 일정 */}
          {selectedDateTodos.length > 0 && (
            <div className="selected-date-schedule">
              <h3>{selectedDate}의 일정</h3>
              <ul>
                {selectedDateTodos.map((todo) => (
                  <li key={todo.todoId} onClick={() => openModal(todo)}>
                    {todo.title}
                  </li>
                ))}
              </ul>
            </div>
          )}

          <div className="upcoming-schedule">
            <h3>다가오는 일정</h3>
            {loading ? (
              <p>로딩 중...</p>
            ) : upcomingTodos.length === 0 ? (
              <p>다가오는 일정이 없습니다.</p>
            ) : (
              <ul>
                {upcomingTodos.slice(0, 3).map((todo) => (
                  <li key={todo.todoId}>
                    {todo.title} - {new Date(todo.startDate).toLocaleDateString()}
                  </li>
                ))}
              </ul>
            )}
          </div>

          <button onClick={() => navigate('/create-todo', { state: { date: selectedDate } })}>
            일정 추가
          </button>
        </div>
      </div>

      {/* 모달 창 */}
      {showModal && selectedTodo && (
        <div className="modal">
          <div className="modal-content">
            <h3>{selectedTodo.title}</h3>
            <p><strong>시작일:</strong> {new Date(selectedTodo.startDate).toLocaleDateString()}</p>
            <p><strong>종료일:</strong> {new Date(selectedTodo.endDate).toLocaleDateString()}</p>
            <p><strong>내용:</strong> {selectedTodo.description}</p>
            <button onClick={handleEdit}>수정하기</button>
            <button onClick={handleDelete}>삭제하기</button>
            <button onClick={closeModal}>닫기</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CalendarComponent;
