import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import "./createTodo.css";
import "react-datepicker/dist/react-datepicker.css"; // 스타일 추가

const CreateTodoPage = () => {
  const location = useLocation();
  const { date } = location.state || {}; // 전달된 날짜 정보 받기
  const [todoTitle, setTodoTitle] = useState('');
  const [startDate, setStartDate] = useState(date ? new Date(date) : new Date());  // 시작일
  const [endDate, setEndDate] = useState(new Date());  // 종료일

  const handleSubmit = () => {
    axios.post('http://localhost:8888/api/todos', { 
      date: startDate, 
      endDate: endDate, 
      title: todoTitle 
    })
    .then(response => {
      // 성공 시 일정 추가 후 처리 (예: 목록 페이지로 돌아가기)
      console.log('Todo added successfully');
    })
    .catch(error => console.error('Error adding todo:', error));
  };

  return (
    <div className="create-todo-container">
      <h2 className="create-todo-title">일정 추가</h2>
      
      <div className="create-todo-date-section">
        <p className="create-todo-date-label">시작 날짜:</p>
        <DatePicker
          selected={startDate}
          onChange={(date) => setStartDate(date)}
          dateFormat="yyyy-MM-dd"
          className="create-todo-date-picker"
        />
      </div>
      
      <div className="create-todo-date-section">
        <p className="create-todo-date-label">종료 날짜:</p>
        <DatePicker
          selected={endDate}
          onChange={(date) => setEndDate(date)}
          dateFormat="yyyy-MM-dd"
          minDate={startDate} // 종료일이 시작일 이전으로 선택되지 않도록 제한
          className="create-todo-date-picker"
        />
      </div>

      <div className="create-todo-date-display">
        <p>날짜: {startDate.toLocaleDateString()} ~ {endDate.toLocaleDateString()}</p>
      </div>

      <div className="create-todo-input-section">
        <input
          type="text"
          value={todoTitle}
          onChange={(e) => setTodoTitle(e.target.value)}
          placeholder="일정 제목"
          className="create-todo-input"
        />
      </div>
      
      <button onClick={handleSubmit} className="create-todo-button">추가</button>
    </div>
  );
};

export default CreateTodoPage;
