import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './calendar.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8888/api';

const CalendarComponent = () => {
  const [date, setDate] = useState(new Date());
  const [todos, setTodos] = useState([]);
  const [upcomingTodos, setUpcomingTodos] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const user = JSON.parse(sessionStorage.getItem('user') || '{}');
  con
  useEffect(() => {
    if (!user) {
      navigate('/');
    }
  }, [user, navigate]);

  const handleLogout = () => {
    sessionStorage.removeItem('user');
    navigate('/');
  };

  // 일정 가져오기 (날짜에 맞춰)
  const fetchTodos = async (selectedDate) => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/todos/mem/${user.memId}`, {
        params: { date: selectedDate },
      });
      setTodos(response.data[selectedDate]);  // 날짜별 일정으로 데이터 처리

      // 다가오는 일정 가져오기
      const allTodosResponse = await axios.get(`${API_BASE_URL}/todos/mem/${user.memId}`);
      const upcoming = allTodosResponse.data.filter((todo) => new Date(todo.startDate) > new Date());
      setUpcomingTodos(upcoming.sort((a, b) => new Date(a.startDate) - new Date(b.startDate))); // 날짜 기준으로 정렬
    } catch (error) {
      alert('일정을 가져오는 중 문제가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  const handleDateChange = (date) => {
    setDate(date);
    setSelectedDate(date.toISOString().split('T')[0]);  // ISO 형식으로 변환
  };

  // eslint-disable-next-line react-hooks/exhaustive-deps
  useEffect(() => {
    if (selectedDate) {
      fetchTodos(selectedDate);
    }
  }, [selectedDate]);


  return (
    <div className="calendar-container">
      <header>
        <h2>To-Do List</h2>
        <p>{user.nickname}</p>
        <button onClick={handleLogout}>Logout</button>
      </header>

      <div className="calendar-wrapper">
        <div className="calendar-left">
          <Calendar onChange={handleDateChange} value={date} />
        </div>

        <div className="calendar-right">
          <div className="today-schedule">
            <h3>오늘의 일정</h3>
            {loading ? (
              <p>Loading...</p>
            ) : (
              <>
                {todos.length === 0 ? (
                  <p>오늘의 일정이 없습니다.</p>
                ) : (
                  <ul>
                    {todos.map((todo) => (
                      <li key={todo.todoId}>{todo.title}</li>
                    ))}
                  </ul>
                )}
              </>
            )}
          </div>

          <div className="upcoming-schedule">
            <h3>다가오는 일정</h3>
            {upcomingTodos.length === 0 ? (
              <p>다가오는 일정이 없습니다.</p>
            ) : (
              <ul>
                {upcomingTodos.slice(0, 3).map((todo) => ( // 가장 가까운 3개 일정만 표시
                  <li key={todo.todoId}>
                    {todo.title} - {new Date(todo.startDate).toLocaleDateString()}
                  </li>
                ))}
              </ul>
            )}
          </div>

          <button onClick={() => navigate('/create-todo', { state: { date: selectedDate } })}>
            일정 추가
          </button>
        </div>
      </div>
    </div>
  );
};

export default CalendarComponent;
