import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './calendar.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8888/api';

const CalendarComponent = () => {
  const [date, setDate] = useState(new Date());
  const [allTodos, setAllTodos] = useState([]); // 전체 일정 상태
  const [todos, setTodos] = useState([]); // 선택한 날짜의 일정
  const [upcomingTodos, setUpcomingTodos] = useState([]); // 다가오는 일정
  const [loading, setLoading] = useState(true); // 로딩 상태
  const [selectedDate, setSelectedDate] = useState(null); // 선택된 날짜
  const navigate = useNavigate();
  const user = JSON.parse(sessionStorage.getItem('user') || '{}');

  // 로그인 검증
  useEffect(() => {
    if (!user.memId) {
      navigate('/');
    } else {
      fetchAllTodos();
    }
  }, [user, navigate]);

  // 전체 일정 가져오기
  const fetchAllTodos = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/todos/mem/${user.memId}`);
      const todosData = response.data;
      setAllTodos(todosData);
      filterUpcomingTodos(todosData);
    } catch (error) {
      alert('일정을 가져오는 중 문제가 발생했습니다. 다시 시도해주세요.');
    } finally {
      setLoading(false);
    }
  };

  // 선택된 날짜의 일정 필터링
  const filterTodosByDate = (selectedDate) => {
    const filtered = allTodos.filter((todo) => {
      const todoDate = new Date(todo.startDate).toISOString().split('T')[0];
      return todoDate === selectedDate;
    });
    setTodos(filtered);
  };

  // 다가오는 일정 필터링
  const filterUpcomingTodos = (data) => {
    const upcoming = data.filter((todo) => new Date(todo.startDate) > new Date());
    setUpcomingTodos(upcoming.sort((a, b) => new Date(a.startDate) - new Date(b.startDate)));
  };

  // 날짜 선택 핸들러
  const handleDateChange = (date) => {
    setDate(date);
    const isoDate = date.toISOString().split('T')[0];
    setSelectedDate(isoDate);
    filterTodosByDate(isoDate);
  };

  // 로그아웃 핸들러
  const handleLogout = () => {
    sessionStorage.removeItem('user');
    navigate('/');
  };

  return (
    <div className="calendar-container">
      <header>
        <h2>To-Do List</h2>
        <p>{user.nickname}</p>
        <button onClick={handleLogout}>Logout</button>
      </header>

      <div className="calendar-wrapper">
        <div className="calendar-left">
          <Calendar onChange={handleDateChange} value={date} />
        </div>

        <div className="calendar-right">
          <div className="today-schedule">
            <h3>오늘의 일정</h3>
            {loading ? (
              <p>로딩 중...</p>
            ) : todos.length === 0 ? (
              <p>오늘의 일정이 없습니다.</p>
            ) : (
              <ul>
                {todos.map((todo) => (
                  <li key={todo.todoId}>{todo.title}</li>
                ))}
              </ul>
            )}
          </div>

          <div className="upcoming-schedule">
            <h3>다가오는 일정</h3>
            {loading ? (
              <p>로딩 중...</p>
            ) : upcomingTodos.length === 0 ? (
              <p>다가오는 일정이 없습니다.</p>
            ) : (
              <ul>
                {upcomingTodos.slice(0, 3).map((todo) => (
                  <li key={todo.todoId}>
                    {todo.title} - {new Date(todo.startDate).toLocaleDateString()}
                  </li>
                ))}
              </ul>
            )}
          </div>

          <button onClick={() => navigate('/create-todo', { state: { date: selectedDate } })}>
            일정 추가
          </button>
        </div>
      </div>
    </div>
  );
};

export default CalendarComponent;
