import React, { useState, useEffect } from 'react';
import { GoogleLogin } from '@react-oauth/google';
import axios from 'axios'; // axios 임포트
import { useNavigate } from 'react-router-dom'; // 페이지 이동을 위한 훅

const LoginPage = () => {
  const [isNicknameModalOpen, setNicknameModalOpen] = useState(false); // 닉네임 입력창 상태
  const [userInfo, setUserInfo] = useState(null); // 로그인 후 사용자 정보 저장
  const [nickname, setNickname] = useState(''); // 사용자 닉네임
  const navigate = useNavigate(); // 페이지 이동을 위한 네비게이트

  // 사용자 정보 저장 함수
  const saveUserInfo = async (userData) => {
    try {
      const apiUrl = userData.provider === 'google'
        ? 'http://localhost:8888/api/login/google'
        : 'http://localhost:8888/api/login/kakao';
  
      await axios.post(apiUrl, userData);
      alert('회원가입이 완료되었습니다.');
      setNicknameModalOpen(false); // 닉네임 입력창 닫기
      navigate('/calendar'); // 캘린더 페이지로 이동
    } catch (error) {
      console.error('Error saving user info:', error);
      alert('회원가입에 실패했습니다. 다시 시도해주세요.');
    }
  };

  // Google 로그인 성공 시 호출되는 함수
  const handleGoogleLoginSuccess = async (response) => {
    console.log('Google Login Success:', response);
  
    const base64Url = response.credential.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    const decoded = JSON.parse(atob(base64));
    const email = decoded.email; // 이메일
    const nickname = decoded.given_name; // 구글에서 받은 닉네임
  
    setUserInfo({
      email,
      nickname,
      tokenId: decoded.sub,
      provider: 'google',
    });

    // 사용자 존재 여부 확인 후 처리
    const userExists = await checkIfUserExists(email);

    if (userExists) {
      alert('이미 가입된 사용자입니다.');
      navigate('/calendar'); // 기존 사용자라면 캘린더 페이지로 이동
    } else {
      setNicknameModalOpen(true); // 신규 사용자라면 닉네임 입력창 열기
    }
  };

  // Kakao 로그인 성공 시 호출되는 함수
  const handleKakaoLoginSuccess = async () => {
    if (!window.Kakao || !window.Kakao.isInitialized()) {
      alert('Kakao SDK가 초기화되지 않았습니다.');
      return;
    }

    window.Kakao.Auth.login({
      success: async (response) => {
        console.log('Kakao Login Success:', response);
        const token = response.access_token;

        // 사용자 정보 가져오기
        try {
          const userInfo = await window.Kakao.API.request({
            url: '/v2/user/me',
          });

          const email = userInfo.kakao_account.email;

          setUserInfo({
            email: email,
            tokenId: token,
            provider: 'kakao',
          });

          // 사용자 존재 여부 확인 후 처리
          const userExists = await checkIfUserExists(email);

          if (userExists) {
            alert('이미 가입된 사용자입니다.');
            navigate('/calendar'); // 기존 사용자라면 캘린더 페이지로 이동
          } else {
            setNicknameModalOpen(true); // 신규 사용자라면 닉네임 입력창 열기
          }
        } catch (error) {
          console.error('Error fetching Kakao user info:', error);
          alert('사용자 정보를 가져오는 중 오류가 발생했습니다.');
        }
      },
      fail: (error) => {
        console.error('Kakao Login Failed:', error);
        alert('Kakao 로그인에 실패했습니다.');
      },
    });
  };

  // 사용자가 존재하는지 확인하는 함수
  const checkIfUserExists = async (email) => {
    try {
      const response = await axios.post('http://localhost:8888/api/login/check-user', { email });
      return response.data.exists; // true이면 이미 존재하는 사용자
    } catch (error) {
      console.error('Error checking if user exists:', error);
      alert('사용자 확인에 실패했습니다.');
      return false;
    }
  };

  // 닉네임 입력 변경 핸들러
  const handleNicknameChange = (event) => {
    setNickname(event.target.value);
  };

  // 닉네임 중복 검사 및 사용자 정보 저장
  const handleNicknameSubmit = async () => {
    try {
      const result = await axios.post('http://localhost:8888/api/login/check-nickname', { nickname });

      if (result.data.exists) {
        alert('이미 사용 중인 닉네임입니다. 다른 닉네임을 선택하세요.');
      } else {
        const userData = { ...userInfo, nickname };
        await saveUserInfo(userData); // 사용자 정보 저장
      }
    } catch (error) {
      console.error('Error saving nickname:', error);
      alert('회원가입에 실패했습니다. 다시 시도해주세요.');
    }
  };

  // 카카오 SDK 초기화
  useEffect(() => {
    if (!window.Kakao) {
      const script = document.createElement('script');
      script.src = 'https://developers.kakao.com/sdk/js/kakao.min.js';
      script.async = true;
      script.onload = () => {
        if (window.Kakao) {
          window.Kakao.init(process.env.REACT_APP_KAKAO_KEY);
          console.log('Kakao SDK initialized');
        }
      };
      document.body.appendChild(script);
      return () => {
        document.body.removeChild(script);
      };
    }
  }, []);

  return (
    <div className="login-container">
      <h2>Login Page</h2>

      {/* Google 로그인 버튼 */}
      <GoogleLogin
        onSuccess={handleGoogleLoginSuccess}
        onError={() => alert('Google 로그인에 실패했습니다. 다시 시도해주세요.')}
        theme="outline" // 버튼 스타일 설정 (예: outline)
      />

      {/* Kakao 로그인 버튼 */}
      <button onClick={handleKakaoLoginSuccess}>Kakao Login</button>

      {/* 닉네임 입력창 */}
      {isNicknameModalOpen && (
        <div className="nickname-modal">
          <h3>닉네임을 입력해주세요</h3>
          <input
            type="text"
            value={nickname}
            onChange={handleNicknameChange}
            placeholder="닉네임"
          />
          <button onClick={handleNicknameSubmit}>확인</button>
          <button onClick={() => setNicknameModalOpen(false)}>취소</button>
        </div>
      )}
    </div>
  );
};

export default LoginPage;
